api.adaptor:
if (payload.errors) {
    payload.status = 2;
    let error = payload.errors[0];
    let errorMessage = error.message || error.errorMessage;
    payload.msg = window.t ? window.t(errorMessage) : errorMessage;
}
var defaultNodes = payload.data && payload.data.config.defaultNodes;
if (defaultNodes) {
    defaultNodes.forEach(function (node, index) {
        if (node.data?._labelFunction) {
            let func = new Function(node.data._labelFunction);
            defaultNodes[index].data.label = func();
            delete defaultNodes[index].data._labelFunction;
        } else {
            defaultNodes[index].data.label = "";
        }
    })
    payload.data.config.defaultNodes = defaultNodes;
}
return payload;
